mapper.select:
select R.*, P.*
from review R
left join product P
on R.productNum = P.productNum
where R.reviewee=#{reviewee}

 and R.type=#{type}

order by R.reviewNum desc
limit #{startRow}, #{perPage}

select count(reviewNum) from review 
where 
reviewNum>0

	and	reviewee=#{reviewee}


	and	reviewer=#{reviewer}


	and type=#{type}
