mapper.select:
select R.*, P.*, M.*, MF.* from review R left join product P on R.productNum = P.productNum left join member M on R.reviewer = M.username left join memberFile MF on R.reviewer = MF.username where R.reviewee=#{reviewee}
and R.type=#{type}
order by R.reviewNum desc limit #{startRow}, #{perPage}

select count(reviewNum) from review 
where reviewee=#{reviewee}

 and type=#{type}

and reviewNum>0
